        src = os.path.join(BACKEND_CSV_DIR, filename)
        dst = os.path.join(FRONTEND_PUBLIC_DIR, filename)
        if os.path.exists(src):
            shutil.copy2(src, dst)
            print(f"Copiado: {filename}")
